import React, { Component } from 'react';
import DetailsModal from "../DetailsModal/DetailsModal";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';
import "./ContactItem.css";

class ContactItem extends Component {
    // To implement the swipe functionality, need a direct reference to the DOM element.
    // Save a reference by using the ref attribute
    listElement;
    deleteElement;
    background;
    wrapper;
    startTime;

    background = null;
    listElement = null;
    deleteElement = null;
    wrapper = null;

    constructor(props){
        super(props);
        this.state = {
            contact: this.props.contact,
            status: "Card",
            startX: 0,
            left: 0,
            isDragged: false,
            isToggleOn: false,
            isSwiped: false,
        }
    }

    componentDidMount() {
        window.addEventListener("mouseup", this.onDragEndMouse);
        window.addEventListener("touchend", this.onDragEndTouch);
    }

    componentWillUnmount() {
        // window.removeEventListener("mouseup", this.onDragEndMouse);
        // window.removeEventListener("touchend".this.onDragEndTouch);
        // this.listElement.  = null;
        // this.deleteElement.div.ref = null;
        // console.log("test uuuu",this.listElement)
        // elem.parentNode.removeChild(elem);
        // console.log("test ubdxjkneiuc",this.listElement.parentNode)
        console.log("test0",this.wrapper);

        console.log("test1",this.listElement.parentNode)
        console.log("test2",this.listElement.parentNode.parentNode)
        console.log("test3",this.listElement.parentNode.parentNode.parentNode)

        // this.wrapper.reomveChild(this.listElement);
        // this.listElement.parentNode.parentNode.parentNode.removeChild(this.listElement.parentNode.parentNode)
        this.wrapper.parentNode.style.display='none'
        // this.deleteElement.parentNode.parentNode.removeChild(this.deleteElement.parentNode)
        // console.log("test vvvvv",this.listElement)

    }

    onDragStart = (x) => {
        this.setState({isDragged: true, startX: x});
        // this.listElement.className = "Card";
        this.setState({status: "Card"});
        this.startTime = Date.now();
        requestAnimationFrame(this.updatePosition);
    }

    onDragEnd = () => {
        if(this.state.isDragged){
            this.setState({isDragged: false});
            if(this.state.left < -66){
                if(!this.state.isSwiped){
                    this.deleteElement.style.transform = `translateX(${0}px)`;
                    this.setState({left: -66,isSwiped: true},()=>{
                        this.listElement.style.transform = `translateX(${0}px)`;
                    });
                }

                // this.wrapper.style.maxHeight = 0;
                // this.onSwiped();
            } else {
                if(!this.state.isSwiped){
                    this.setState({left:0});
                    this.listElement.style.transform = `translateX(${this.state.left}px)`;
                }
            }
        }
    }

    onDragStartMouse = (e) => {
        this.onDragStart(e.clientX);
        window.addEventListener("mousemove",this.onMouseMove);
    }

    onDragEndMouse = (e) => {
        window.removeEventListener("mousemove",this.onMouseMove);
        this.onDragEnd();
    }

    onDragStartTouch = (e) => {
        const touch = e.targetTouches[0];
        this.onDragStart(touch.clientX);
        window.addEventListener("touchmove",this.onTouchMove);
    }

    onDragEndTouch = (e) => {
        window.removeEventListener("touchmove",this.onTouchMove);
        this.onDragEnd();
    }

    onMouseMove = (e) => {
        const left = e.clientX - this.state.startX;
        if(left<0){
            this.setState({left: left});
        }
    }

    onTouchMove = (e) => {
        const touch = e.targetTouches[0];
        const left = touch.clientX - this.state.startX;
        if(left<0){
            this.setState({left: left});
        }
    }

    onSwiped = () => {
        if(this.props.onSwipe) {
            this.props.onSwipe();
        }
    }

    onClicked = () => {
        if(this.props.onSwipe) {
            this.props.onSwipe();
        }
    }

    updatePosition = () => {
        if(this.state.isDragged) {
            requestAnimationFrame(this.updatePosition);
        }

        const time = Date.now();
        const elapsedTime = time - this.startTime; 

        if(this.state.isDragged && (elapsedTime > 1000/60)) {
            this.listElement.style.transform = `translateX(${this.state.left}px)`;
            this.deleteElement.style.transform = `translateX(${this.state.left}px)`;
            this.startTime = Date.now();
        }
    }

    showDetails = (contactDetails) => {
        this.setState(state => ({
            isToggleOn: !state.isToggleOn
        }));
        console.log("clicked a card",contactDetails);
    }


    render() {
        return (
            <div className="Wrapper">
            {/* <div className="Wrapper" ref={div => (this.wrapper = div)}> */}

                {
                    this.state.isToggleOn && <DetailsModal contact={this.state.contact}></DetailsModal>
                }

                <div 
                    className = {this.state.isSwiped ? "swipedCard":"Card"}
                    ref = {div => {
                        this.listElement = div     
                    }}
                    onClick = {this.onClicked}
                    onDoubleClick = {this.showDetails.bind(this,this.state.contact)}
                    onMouseDown = {this.onDragStartMouse}
                    onTouchStart = {this.onDragStartTouch}
                >
                    <img className="ContactImage" src={this.state.contact.image_url} alt="contact"/>
                    <div className = "Details">
                        <div className="contactName">
                            {this.state.contact.first_name} {this.state.contact.last_name}
                        </div>
                        <div className="contactPhone">
                            {this.state.contact.phone}
                        </div>
                        <div className="contactCompany">
                            {this.state.contact.company}
                        </div>
                    </div>
                </div>

                <div className="DeleteBtn" ref = {div => (this.deleteElement = div)} onClick={()=>this.props.onDeleteContact(this.state.contact.id)}>
                    <FontAwesomeIcon icon={faTrash} />
                </div>
            </div>
        );
    }
}

export default ContactItem;
