import React, { Component } from 'react';
import { Card, Button, Modal} from 'react-bootstrap';
import { CardInfo, Background, ContactImage, ContactDetails, Title1, Title2 } from "./DetailsModalStyle";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMale, faFemale, faPhone, faAt, faBriefcase } from '@fortawesome/free-solid-svg-icons';



class DetailsModal extends Component{
    render() {
        return (
            <React.Fragment>
                <Modal size="sm" show={this.props.isToggleOn} onHide={()=>this.props.handleClose()}>
                    <Card>
                        <CardInfo>
                            <Background></Background>
                            <ContactImage src={this.props.contact.image_url}></ContactImage>
                            <ContactDetails>
                                <Title1>{this.props.contact.first_name} {this.props.contact.last_name}</Title1>
                                <Title2>
                                    {
                                        this.props.contact.gender === "Male"?<FontAwesomeIcon icon={faMale}/>:<FontAwesomeIcon icon={faFemale}/>
                                    }
                                    &nbsp;{this.props.contact.gender}</Title2>
                                <Title2><FontAwesomeIcon style={{color: "red"}} icon={faBriefcase}/>&nbsp;{this.props.contact.company}</Title2>
                                <Title2><FontAwesomeIcon icon={faPhone}/>&nbsp;{this.props.contact.phone}</Title2>
                                <Title2><FontAwesomeIcon icon={faAt}/>&nbsp;{this.props.contact.email}</Title2>
                            </ContactDetails>
                        </CardInfo>
                        <Button variant="secondary" block onClick={()=>this.props.handleClose()}>Ok</Button>   
                    </Card>
                </Modal>
            </React.Fragment>
        );
    }
}

export default DetailsModal;
