import React, { Component } from 'react';
import ContactItem from "../ContactItem/ContactItem";
import { List, Wrapper } from './ContactListStyle';

class ContactList extends Component {

    constructor(props) {
        super(props);
        // const contacts = Array.prototype.map.call(this.props.contacts, (contact, index) => {
        //     if (index < 4) return contact
        // })
        this.state = {
            arr: []
        }
    }

    componentWillReceiveProps(nextProps){
        const newContacts = [...nextProps.contacts];
        this.setState({arr: [...newContacts]});
    }


    onDeleteContact = (id)=> {
        let newContacts = Array.prototype.filter.call(this.state.arr ,(contact, index)=> {
            if (index < 4) return contact.id !== id
        })
        this.setState({arr: [...newContacts]});
    }

    render() {
        return (
            <Wrapper>
                <List>
                    {Array.prototype.map.call(this.state.arr, (contact, index) => {
                        if (index < 4) return <ContactItem key={index} contact={contact} index={index} onDeleteContact={this.onDeleteContact}></ContactItem>
                    }
                    )}
                </List>
            </Wrapper>
        );
    }
}

export default ContactList;
