import React, { useState } from 'react';
import { Card, Button, Modal} from 'react-bootstrap';
import { CardInfo, Background, ContactImage, ContactDetails, Title } from "./DetailsModalStyle";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMale, faFemale, faPhone, faAt, faBriefcase } from '@fortawesome/free-solid-svg-icons';



function DetailsModal(props){
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    return (
        <React.Fragment>
            {/* <Button variant="primary" onClick={handleShow}>
                Show Details
            </Button> */}

            <Modal size="sm" show={true} onHide={handleClose}>
                <Card>
                    <CardInfo>
                        <Background></Background>
                        <ContactImage src={props.contact.image_url}></ContactImage>
                        <ContactDetails>
                            <Title>{props.contact.first_name} {props.contact.last_name}</Title>
                            <Title>
                                {
                                    props.contact.gender === "Male"?<FontAwesomeIcon icon={faMale}/>:<FontAwesomeIcon icon={faFemale}/>
                                }
                                &nbsp;{props.contact.gender}</Title>
                            <Title><FontAwesomeIcon icon={faBriefcase}/>&nbsp;{props.contact.company}</Title>
                            <Title><FontAwesomeIcon icon={faPhone}/>&nbsp;{props.contact.phone}</Title>
                            <Title><FontAwesomeIcon icon={faAt}/>&nbsp;{props.contact.email}</Title>
                        </ContactDetails>
                    </CardInfo>
                    <Button variant="secondary" block onClick={handleClose}>Ok</Button>   
                </Card>
            </Modal>
        </React.Fragment>
    );
}

export default DetailsModal;
