import React, { Component } from 'react';
import ContactItem from "../ContactItem/ContactItem";
import { List, Wrapper } from './ContactListStyle';

class ContactList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            arr: []
        }
    }

    componentWillReceiveProps(nextProps){
        console.log("nextprops:",nextProps);
        let arr = [];
        Array.prototype.forEach.call(nextProps.contacts,(item,index)=>{
            if(index <10) arr.push(item)
        })
        // Array.prototype.forEach.call(nextProps.contacts,(item,index)=>{
        //     if(index <10) arr.push(item)
        // })
        console.log("arr inside componentWillReceive: ", arr);
        this.setState({arr:arr},()=> console.log("will receive props"));
        console.log("state.arr inside componentWillReceive: ",this.state.arr);
    }


    onDeleteContact = (id)=> {
        console.log(id);
        // let newContacts = this.state.arr.filter((contact)=>{
        //     return contact.id !== id
        // })
        console.log("before filter in delete",this.props.contacts);
        let newContacts = Array.prototype.filter.call(this.state.arr ,(contact, index)=> {
            if (index < 10) return contact.id !== id
        })
        console.log("after filter in delete",newContacts);
        this.setState({arr:newContacts},()=> console.log("update state"))
        console.log("after delete: ", this.state.arr);
    }

    render() {
        return (
            <Wrapper>
                <List>
                    {Array.prototype.map.call(this.state.arr, (contact, index) => {
                        if (index < 10) return <ContactItem key={index} contact={contact} index={index} onDeleteContact={this.onDeleteContact}></ContactItem>
                    }
                    )}
                </List>
            </Wrapper>
        );
    }
}

export default ContactList;


