import React, { useState } from 'react';
import { Card, Button, Modal, Image } from 'react-bootstrap';

function DetailsModal(props){
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    return (
        <React.Fragment>
            {/* <Button variant="primary" onClick={handleShow}>
                Show Details
            </Button> */}

            <Modal size="sm" show={show} onHide={handleClose}>
                <Card>
                    <Image src={props.contact.image_url} fluid thumbnail />
                    <Card.Body>
                        <Card.Title>{props.contact.first_name} {props.contact.last_name}</Card.Title>
                        <Card.Text>Gender: {props.contact.gender}</Card.Text>
                        <Card.Text>Email: {props.contact.email}</Card.Text>
                        <Card.Text>Company: {props.contact.company}</Card.Text>
                        <Card.Text>Phone: {props.contact.phone}</Card.Text>
                        <Button variant="primary" block onClick={handleClose}>Ok</Button>
                    </Card.Body>
                </Card>
            </Modal>
        </React.Fragment>
    );
}

export default DetailsModal;


